definition.script:
@Library('github.com/syndesisio/syndesis-pipeline-library')
def parameters = [:]

node {
    slave {
        withArbitraryUser () {
            withOpenshift {
                withMaven (mavenSettingsXmlSecret: 'm2-settings', mavenLocalRepositoryPath: '/home/jenkins/mvnrepo/', serviceAccount: 'jenkins') {
                withSshKeys {
                        withGpgKeys (image: 'centos:centos7') {
                            inside {
                                git 'git@github.com:syndesisio/syndesis-rest.git'
                                container('maven') {
                                    sh 'git config --global user.email "rhipaasuser@gmail.com"'
                                    sh 'git config --global user.name "Syndesis CI"'
                                    sonatypeRelease(enabled: mavenReleaseEnabled, releaseVersion: "${releaseVersion}", developmentVersion: "${developmentVersion}", profiles: "fabric8,ci,release", stagingProgressTimeoutMinutes: '10')

                                   tag(sourceProject: 'syndesis-ci', sourceImageStream: 'syndesis-rest', sourceTag: "${releaseVersion}", targetProject: 'syndesis-ci', targetImageStream: 'syndesis-rest', targetTag: 'latest')
                                   tag(sourceProject: 'syndesis-ci', sourceImageStream: 'syndesis-rest', sourceTag: "${releaseVersion}", targetProject: 'syndesis-ci', targetImageStream: 'syndesis-rest', targetTag: '1.0')
                                   ["1.0", "${releaseVersion}", "latest"].each {
                                      imagePush(enabled: dockerReleaseEnabled, name: "syndesis-rest-${it}-push-dockerhub-bc", user:'syndesis', repo: 'syndesis-rest', tag: "${it}", imageStreamTag: "syndesis-rest:${it}")
                                   }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}
